def leads(request):
    enquiries = None
    if request.method == 'POST':
        form = DateRangeForm(request.POST)
        if form.is_valid():
            start_date = form.cleaned_data['start_date']
            end_date = form.cleaned_data['end_date']
            enquiries = Prospect.objects.filter(created_date__range=[start_date, end_date])
    else:
        form = DateRangeForm()
    return render(RequestContext(request), 'products/leads.html', {'form': form, 'enquiries': enquiries})
